import React from 'react';
import NavBar from '../src/components/navbar';
import { BrowserRouter } from "react-router-dom";
import Login from '../src/components/login';
import CreateAccount from '../src/components/createaccount';
import AllData from '../src/components/alldata';
import Account from '../src/components/deposit';
import AccountWithdraw from '../src/components/withdraw';
import Balance from '../src/components/balance';
import ReactDOM from "react-dom"

function Spa(){
  const [user, setUser] = React.useState();

  React.useEffect(() => { 
      const url = `/account/info`;
      (async () => {
        var res = await fetch(url);
        var data = await res.json();
        console.log("User is ", data);
        setUser(data.user);
    })();

    }, []);

  return ( //<NavBar/>
      <React.HashRouter>
      <div>
           <NavBar user={user} setUser={setUser}/>
          <React.UserContext.Provider value={{users:[{name:'abel',email:'abel@mit.edu',password:'secret',balance:100}]}}>

              <div className="container" style={{padding: "70px"}}>
                  <React.Route path="/" exact component={() => <Login setUser={setUser} />} />
                  <React.Route path="/CreateAccount/" component={CreateAccount} />
                  <React.Route path="/alldata/" component={() => <AllData user={user} />}  />
                  <React.Route path="/login" component={() => <Login setUser={setUser} />}  />
                  <React.Route path="/Deposit/" component={() => <Account user={user} />}/>
                  <React.Route path="/Withdraw/" component={() => <AccountWithdraw user={user} />}/>
                  <React.Route path="/Balance/" component={Balance} />

              </div>
          </React.UserContext.Provider>
      </div>
      </React.HashRouter>
  )
}

ReactDOM.render(<Spa/>,
  document.getElementById('root')
);
